    console.log('fruitName:', fruitName);

    const image = await Axios.get(`${fruitName}/image`);
    console.log("image:", image);

    // setFrutas(image);
  };

  useEffect(() => {
    const getFruits = async () => {
        const { data } = await Axios.get("all");
        console.log("data:", data);

        for (let i = 0; i < data.length; i++) {
            const fruta = data[i];
            console.log('fruta:', fruta);

            let imagen = await getFruitImage(fruta.name);
            console.log('imagen:', imagen);
        }

        setFrutas(data);
    };

    async function asyncCall() {
      await getFruits();
    }
    asyncCall();
  }, []);

  return (
    <Table responsive hover className="mt-5">
      <thead>
        <tr>
          <th>#</th>
          <th>Imagen</th>
          <th>Nombre</th>
          <th>Familia</th>
          <th>Género</th>
          <th>Calorias</th>
          <th>Carbohidratos</th>
          <th>Grasa</th>
          <th>Proteina</th>
          <th>Azucar</th>
        </tr>
      </thead>

      <tbody>
        { frutas.length > 0 ?
            frutas.map((fruta, index) => 
                <tr key={index}>
                    {/* <td>{getFruitImage(fruta.name)}</td> */}
                    <td>{fruta.name}</td>
                    <td>{fruta.id}</td>
                    <td>{fruta.name}</td>
                    <td>{fruta.family}</td>
                    <td>{fruta.genus}</td>
                    <td>{fruta.nutritions.calories}</td>
                    <td>{fruta.nutritions.carbohydrates}</td>
                    <td>{fruta.nutritions.fat}</td>
                    <td>{fruta.nutritions.protein}</td>
                    <td>{fruta.nutritions.sugar}</td>
                </tr>
            )
            :
            <></>
        }
      </tbody>
    </Table>


